mapper.insert:
INSERT INTO DEP
	( DEP_CODE
	  , DEP_NAME
	  , DEP_UPDE
	  , DEP_USE
	  , DEP_NOTE
	  , DEP_REGM
	  , DEP_REGD )
VALUES ( #{depCode}
	  , #{depName}
	  , #{depUpde}
	  , #{depUse}
	  , #{depNote}
	  , #{depRegm}
	  , SYSDATE() )

mapper.update:
UPDATE DEP
SET  DEP_CODE=#{depCode}
	, DEP_NAME=#{depName}
	, DEP_UPDE=#{depUpde}
	, DEP_USE=#{depUse}
	, DEP_NOTE=#{depNote}
	, DEP_UPTM=#{depUptm}
	, DEP_UPTD=SYSDATE()
	  WHERE DEP_CODE=#{depCode}
