mapper.insert:
INSERT INTO POS
	( POS_CODE
	  , POS_NAME

	  , POS_USE
	  , POS_NOTE
	  , POS_REGM
	  , POS_REGD )
VALUES ( #{posCode}
	  , #{posName}

	  , #{posUse}
	  , #{posNote}
	  , #{posRegm}
	  , SYSDATE() )

mapper.update:
UPDATE POS
SET  POS_CODE=#{posCode}
	, POS_NAME=#{posName}

	, POS_USE=#{posUse}
	, POS_NOTE=#{posNote}
	, POS_UPTM=#{posUptm}
	, POS_UPTD=SYSDATE()
	  WHERE POS_CODE=#{posCode}
